mapper.select:
SELECT * FROM [users]
WHERE u_id=
#{u_id} AND u_qpw=#{u_qpw} AND u_apw=#{u_apw}

SELECT
f.drive_id,f.folder_id,f.folder_name FROM my_join_folder m JOIN folder f
ON m.folder_id=f.folder_id AND m.id=#{id}

SELECT drive_id 
FROM drive
WHERE id=#{id}

mapper.update:
UPDATE [users] SET
u_pw = #{u_pw}
WHERE id=#{id} AND
u_email=#{u_email}
